insert into nave(nombre, tipo_nave, descripcion, caracteristica, medio_visual, nombre_film) values ('USS Enterprise', 'Nave de exploración','La nave USS Enterprise (en sus diversas versiones, como NCC-1701 y NCC-1701-D) es uno de los iconos más reconocidos de la ciencia ficción. La nave se utiliza para misiones de exploración en el espacio, transporte diplomático y defensa.','Capacidad para velocidades warp (más rápidas que la luz), una tripulación diversa y altamente capacitada, y tecnología avanzada como el replicador y el transportador.','SERIE', 'Star Trek');
insert into nave(nombre, tipo_nave, descripcion, caracteristica, medio_visual, nombre_film) values ('X-Wing Starfighter', 'Caza estelar multifuncional de la Alianza Rebelde','Diseñado para misiones de ataque, reconocimiento y combate espacial. Equilibrado en velocidad, armamento y protección, es ideal para una variedad de misiones tácticas.','conocido por su versatilidad y su distintivo diseño de alas en forma de "X" que se despliegan en combate.','SERIE', 'Star Trek');
insert into nave(nombre, tipo_nave, descripcion, caracteristica, medio_visual, nombre_film) values ('Millennium Falcon', 'Carguero ligero modificado','Propiedad de Han Solo y Chewbacca, esta nave es conocida por su apariencia "desgastada" y modificaciones ilegales que la hacen rápida y difícil de detectar.','Motores modificados para altas velocidades, armamento láser, y un hipermotor que permite viajes rápidos entre sistemas estelares.','SERIE', 'Star Wars');
insert into nave(nombre, tipo_nave, descripcion, caracteristica, medio_visual, nombre_film) values ('Nostromo', 'Nave de transporte y carga','La Nostromo es una nave industrial que transporta recursos entre planetas y es el escenario donde se desarrolla el primer contacto con el Alien.','Equipos industriales, poca tecnología militar, y un diseño utilitario que refleja el enfoque industrial y comercial.','PELICULA', 'Alien');
insert into nave(nombre, tipo_nave, descripcion, caracteristica, medio_visual, nombre_film) values ('TARDIS', 'Máquina del tiempo y nave espacial','La TARDIS parece una cabina de policía británica por fuera, pero en realidad es una nave muy avanzada de los Time Lords capaz de viajar en el tiempo y el espacio.','Más grande por dentro que por fuera, indestructible, con tecnología de teletransportación y una "personalidad" propia.','SERIE', 'Doctor Who');